package dynamicprogramming.strings.palindrome;

import java.util.HashMap;
import java.util.Map;



public class LeetCode1960 {
    static int findPalindromeAfterIndex(int startIndex, String s, int leftPointerThreshold) {
        int current = -1;
        for (int i = startIndex; i < s.length(); i++) {
            current = Math.max(current, findPalindromeByIndex(i, s, leftPointerThreshold));
        }
        return current;
    }

    static int findPalindromeByIndex(int startIndex, String s, int leftLimitation) {
        int leftPointer = startIndex, rightPointer = startIndex;
        int maxLength = 0;
        while (leftPointer >= 0 && rightPointer < s.length() && s.charAt(leftPointer) == s.charAt(rightPointer)) {
            maxLength = Math.max((rightPointer + 1) - leftPointer, maxLength);
            if (leftLimitation >= leftPointer - 1) {
                return maxLength;
            }
            leftPointer--;
            rightPointer++;
        }
        return maxLength;
    }

    public static int maxProduct(String s) {
        int product = -1;
        Map<Integer, Integer> cache = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            int current;
            if (cache.containsKey(i)) {
                current = findPalindromeByIndex(i, s, -1);
            } else {
                current = findPalindromeByIndex(i, s, -1);
            }
            int leftPointerThreshold = (current / 2) + i;
            int futureMax = findPalindromeAfterIndex(leftPointerThreshold + 1, s, leftPointerThreshold);
            cache.put(leftPointerThreshold + 1, futureMax);
            product = Math.max(current * futureMax, product);

        }
        return product;
    }

    public static void main(String[] args) {
        System.out.println(maxProduct(""));
        System.out.println(maxProduct("ggbswiymmlevedhkbdhntnhdbkhdevelmmyiwsbgg"));
    }

}
